mapper.select:
SELECT COUNT(*) from buyerinfo
where buyerId = #{buyerId }
and	buyerPw = #{buyerPw }

SELECT * FROM buyerinfo
where buyerName = #{buyerName }
and buyerEmail = #{buyerEmail }

SELECT COUNT(*) FROM buyerinfo
where buyerName = #{buyerName }
and buyerEmail = #{buyerEmail }

SELECT * FROM buyerinfo
where buyerName = #{buyerName }
and buyerEmail = #{buyerEmail }
and buyerId = #{buyerId }

SELECT COUNT(*) FROM buyerinfo
where buyerName = #{buyerName }
and buyerEmail = #{buyerEmail }
and buyerId = #{buyerId }

mapper.update:
update buyerinfo
set buyerPw = #{buyerPw }
where buyerName = #{buyerName }
and buyerEmail = #{buyerEmail }
and buyerId = #{buyerId }

mapper.select:
SELECT COUNT(*) FROM SellerLoc
WHERE 1=1

	AND zone=#{zoneSelect}


	AND station=#{stationSelect}

SELECT * FROM(
	SELECT rownum rnum, b.* FROM(
			SELECT * FROM SellerLoc
			WHERE 1=1

				AND zone=#{zone}


				AND station=#{station}

			ORDER BY locNo desc
		)B 
	ORDER BY rnum
)R
WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT * FROM bookListInfo
WHERE sellerId = #{sellerId }
ORDER BY month

mapper.update:
UPDATE BookListInfo
SET circulation=circulation-#{bookNumber}
WHERE sellerId=#{sellerId } and month=#{bookMonth }

UPDATE reservation
SET status='예약취소(시간초과)'
WHERE reserveNo=#{reserveNo }

mapper.select:
SELECT COUNT(*) FROM sellerLoc
WHERE 1=1

	AND zone=#{zoneSelect}


	AND station=#{stationSelect}

SELECT * FROM(
	SELECT rownum rnum, b.* FROM(
			SELECT * FROM SellerLoc
			WHERE 1=1

				AND zone=#{zone}


				AND station=#{station}

			ORDER BY locNo desc
		)B 
	ORDER BY rnum
)R
WHERE rnum BETWEEN #{startNo} AND #{endNo}

SELECT * FROM(
	SELECT rownum rnum, b.* FROM(
			SELECT * FROM reservation
			WHERE buyerId = #{buyerId}
			ORDER BY reserveNo desc
		)B 
	ORDER BY rnum
)R
WHERE rnum BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE reservation
SET status='예약취소'
WHERE magazineNo=#{magazineNo }

update bookListInfo
set circulation = circulation+#{bookNumber }
where magazineNo = #{magazineNo }
